#include <iostream>

using namespace std;

int i;

int main() {
    cin.tie(0);
    ios::sync_with_stdio(false);

    while (true) {
        string s = "";
        if (i < 506 || i > 510) {
            for (int j = 0; j < 1024 - i; j++) {
                s += (i & j) ? "." : "#";
            }
            cout << s << '\n';
            i++;
        } else {
            string t = "##..##............................................................................................................................................................................................................................................................................................................................................................................................................................................................####..##..##.######..##...##..##.....####...####..###..####...##..##\n#...#............................................................................................................................................................................................................................................................................................................................................................................................................................................................##..##.###.##...##...####..##.##.....##..##.##..##..##.##..##..#...#\n####.............................................................................................................................................................................................................................................................................................................................................................................................................................................................##..##.##.###...##..##..##.####.........##..##..##..##.##..##..####\n#.#..............................................................................................................................................................................................................................................................................................................................................................................................................................................................##..##.##..##...##..######.##.##......##....##..##..##.##..##..#.#\n##................................................................................................................................................................................................................................................................................................................................................................................................................................................................####..##..##...##..##..##.##..##....######..####...##..####...##\n";
            cout << t;
            i = 511;
        }

        if (i == 1024)
            break;
    }
}